mapper.select:
select

,

from blog


        and title like #{title}


        and type_id = #{typeId}


        and DATE_FORMAT(release_date,'%Y年%m月') = #{releaseDateStr}


order by release_date desc


    limit #{start},#{size}

select count(id) from blog


        and title like #{title}


        and type_id = #{typeId}


        and DATE_FORMAT(release_date,'%Y年%m月') = #{releaseDateStr}
